
public class DisplayCharactersFromAToZ {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(char c='A';c<='Z';c++) {
			System.out.println(c);	
		}
		
		
		for(char c='a';c<='z';c++) {
			System.out.println(c);
		}
		
		for(int i='a';i<='z';i++) {
			System.out.println(i);
			
		}
	}

}
